$argList = @('--model',$Model)
if ($WeakModel) { $argList += @('--weak-model',$WeakModel) }
$argList += @('--edit-format',$EditFormat,'--message',$Prompt)
if ($filesResolved) { $argList = $filesResolved + $argList }
$cmd = (Get-Command -Name 'aider' -ErrorAction Stop).Path
$exitCode = 0
Push-Location -LiteralPath $repo
try {
    $proc = Start-Process -FilePath $cmd -ArgumentList $argList -WorkingDirectory $repo -NoNewWindow -RedirectStandardOutput $stdoutPath -RedirectStandardError $stderrPath -Wait -PassThru
    $exitCode = $proc.ExitCode
} finally {
    Pop-Location
}
$outText = (Test-Path -LiteralPath $stdoutPath) ? (To-Ascii (Get-Content -LiteralPath $stdoutPath -Raw)) : ''
$errText = (Test-Path -LiteralPath $stderrPath) ? (To-Ascii (Get-Content -LiteralPath $stderrPath -Raw)) : ''
$meta = [ordered]@{
    timestamp = $stamp
    repo_path = $repo
    prompt_source = $(if ($PromptFile) { (Resolve-Path -LiteralPath $PromptFile).Path } else { 'inline' })
    files = $filesResolved
    model = $Model
    weak_model = $WeakModel
    edit_format = $EditFormat
    branch = $Branch
    save = [bool]$Save
    return_json = [bool]$ReturnJson
    stdout_log = $stdoutPath
    stderr_log = $stderrPath
    exit_code = $exitCode
}
Set-Content -LiteralPath $metaPath -Value (ConvertTo-Json $meta -Depth 4) -Encoding ASCII
if ($ReturnJson) {
    $payload = @{ stdout = $outText; stderr = $errText; exit_code = $exitCode; logs = @{ stdout = $stdoutPath; stderr = $stderrPath; meta = $metaPath } }
    Write-Output (ConvertTo-Json $payload -Depth 4)
} else {
    if ($outText) { Write-Output ($outText.TrimEnd()) }
    if ($errText) { [Console]::Error.WriteLine($errText.TrimEnd()) }
}
exit $exitCode
